// [Template no Kotlin Playground](https://play.kotlinlang.org/#)

val school: String = "DIO - Digital Innovation One"

val listaConteudosEducacionais = mutableListOf<ConteudoEducacional>()

enum class Nivel { BASICO, INTERMEDIARIO, AVANCADO }

data class Usuario(val nome: String)

data class ConteudoEducacional(val nome: String, val duracao: Int) {
	
    fun adicionar() {
    	listaConteudosEducacionais.add(this)
    }
}

data class Formacao(val nome: String, val conteudos: List<ConteudoEducacional>, val nivel: Nivel) {
	
    val inscritos = mutableListOf<Usuario>()
    
    fun matricular(usuario: Usuario) {
        inscritos.add(usuario)
    }
    
    fun obterDados(){
        println(school)
        inscritos.forEach(){
            var duracaoTotal: Int = 0
        	println()
            println("Formação: ${nome}")
            println("Incrito: ${it.nome}")
            println("Nivel: ${nivel}")
            conteudos.forEach(){
                println("Conteúdo: ${it.nome}. Duração: ${it.duracao} horas.")
                duracaoTotal = duracaoTotal + it.duracao
            }
            println("Duração Total: $duracaoTotal horas")
		}
    }
}

fun main() {
    
    // Criar curso
    val formacao = Formacao("Kotlin Experience", listaConteudosEducacionais, Nivel.BASICO)
   	
    // Adicionar conteúdo
    ConteudoEducacional("Princípios de Agilidade e Desenvolvimento de Software", 17).adicionar()
    ConteudoEducacional("Dominando a Linguagem de Programação Kotlin", 11).adicionar()
    ConteudoEducacional("Refinando sua Técnica com Desafios de Código em Kotlin", 5).adicionar()
    ConteudoEducacional("Fortalecendo seu Perfil Profissional", 5).adicionar()
  	
    // Inscrever alunos
    formacao.matricular(Usuario("Alex"))
    formacao.matricular(Usuario("João"))
    formacao.matricular(Usuario("Felipe"))
    formacao.matricular(Usuario("Rafael"))
    formacao.matricular(Usuario("Paulo"))
    formacao.matricular(Usuario("Marcelo"))
    formacao.matricular(Usuario("Venilton"))
    formacao.matricular(Usuario("Alexandre"))
    
    // Imprimir
    formacao.obterDados()
}
